import * as React from 'react';
import {
  ScrollView,
  StyleSheet,
  Text,
  Keyboard,
  PermissionsAndroid,
  type Permission,
} from 'react-native';
import {
  Dialog,
  Card,
  Space,
  Row,
  Col,
  TextInput,
  Button,
  Blank,
  Toast,
} from '@fruits-chain/react-native-xiaoshu';
import PrinterImin, {
  IminPrintAlign,
  IminFontStyle,
  IminTypeface,
  IminBarcodeType,
  IminQrcodeCorrectionLevel,
  IminBarcodeTextPos,
} from 'react-native-printer-imin';
import { InfoCircleOutline } from '@fruits-chain/icons-react-native';
export default function Home() {
  const toastClose = React.useRef<{
    close: () => void;
  }>();
  const getMediaFilePermission = async () => {
    try {
      const granted = await PermissionsAndroid.requestMultiple([
        PermissionsAndroid.PERMISSIONS.READ_EXTERNAL_STORAGE,
        PermissionsAndroid.PERMISSIONS.WRITE_EXTERNAL_STORAGE,
      ] as Permission[]);
      if (
        granted['android.permission.READ_EXTERNAL_STORAGE'] ===
        PermissionsAndroid.RESULTS.granted
      ) {
        console.log('You can use the read External Storage');
      }
      if (
        granted['android.permission.WRITE_EXTERNAL_STORAGE'] ===
        PermissionsAndroid.RESULTS.granted
      ) {
        console.log('You can use the write External Storage');
      }
    } catch (e) {
      console.error(e);
    }
  };
  const [dialogState, setDialogState] = React.useState({
    cDialog: {
      value: '',
      show: false,
    },
  });
  const [open, setOpen] = React.useState<boolean>(true);
  const closeDialog = React.useCallback(() => {
    Keyboard.dismiss();
    setDialogState((s) => ({
      ...s,
      cDialog: {
        ...s.cDialog,
        show: false,
      },
    }));
  }, []);
  const handleConfirm = React.useCallback(async () => {
    Keyboard.dismiss();
    setDialogState((s) => ({
      ...s,
      cDialog: {
        ...s.cDialog,
        show: false,
      },
    }));
    await PrinterImin.printText(dialogState.cDialog.value, {
      fontSize: 20,
      align: IminPrintAlign.left,
      fontStyle: IminFontStyle.boldItalic,
      typeface: IminTypeface.Monospace,
    });
    await PrinterImin.printAndFeedPaper(100);
    await PrinterImin.sendRAWDataHexStr('0A');
    await PrinterImin.partialCut();
  }, [dialogState.cDialog.value]);
  return (
    <>
      <ScrollView>
        <Space direction="vertical">
          <Card>
            <Row gap={6} justify="flex-start">
              <Col span={12}>
                <Text
                  style={styles.item}
                  onPress={async () => await PrinterImin.initPrinter()}
                >
                  initPrinter
                </Text>
              </Col>
              <Col span={12}>
                <Text
                  style={styles.item}
                  onPress={async () => {
                    const info = await PrinterImin.getPrinterStatus();
                    if (info) {
                      if (toastClose.current) {
                        toastClose.current.close();
                      }
                      toastClose.current = Toast({
                        type: 'icon',
                        message: `${info.message}`,
                        icon: <InfoCircleOutline size={40} color="#f30" />,
                      });
                    }
                  }}
                >
                  getPrinterStatus
                </Text>
              </Col>
              <Col span={12}>
                <Text
                  style={styles.item}
                  onPress={() => {
                    setDialogState((s) => ({
                      ...s,
                      cDialog: {
                        ...s.cDialog,
                        value:
                          'iMin advocates the core values of "Integrity, Customer First, Invention&Creation, Patience”, using cloud-based technology to help businesses to get  access to the Internet and also increases their data base, by providing more solutions so that their business can take a step further. Through their efficiency enhancement, cost improvement, service innovation, and  better services for consumers, these aspect will drives the entire industry development.',
                        show: true,
                      },
                    }));
                  }}
                >
                  PrinterText
                </Text>
              </Col>
              <Col span={12}>
                <Text
                  style={styles.item}
                  onPress={async () => {
                    await PrinterImin.printAntiWhiteText(
                      'iMin advocates the core values of "Integrity, Customer First, Invention&Creation, Patience”.',
                      {
                        fontSize: 30,
                        align: IminPrintAlign.center,
                      }
                    );
                    await PrinterImin.printAndFeedPaper(100);
                    await PrinterImin.sendRAWDataHexStr('0A');
                  }}
                >
                  printAntiWhiteText
                </Text>
              </Col>
              <Col span={12}>
                <Text
                  style={styles.item}
                  onPress={async () =>
                    await PrinterImin.setTextLineSpacing(1.0)
                  }
                >
                  setTextLineSpacing
                </Text>
              </Col>
              <Col span={12}>
                <Text
                  style={styles.item}
                  onPress={async () => {
                    await PrinterImin.printColumnsText([
                      {
                        text: 'Test',
                        width: 2,
                        fontSize: 26,
                        align: IminPrintAlign.left,
                      },
                      {
                        text: 'Description Description Description@48',
                        width: 6,
                        fontSize: 26,
                        align: IminPrintAlign.left,
                      },
                      {
                        text: '192.00',
                        width: 2,
                        fontSize: 26,
                        align: IminPrintAlign.right,
                      },
                    ]);
                    await PrinterImin.sendRAWDataHexStr('0A');
                    await PrinterImin.printColumnsText([
                      {
                        text: 'Test',
                        width: 2,
                        fontSize: 26,
                        align: IminPrintAlign.left,
                      },
                      {
                        text: 'Description Description Description@48',
                        width: 6,
                        fontSize: 26,
                        align: IminPrintAlign.left,
                      },
                      {
                        text: '192.00',
                        width: 2,
                        fontSize: 26,
                        align: IminPrintAlign.right,
                      },
                    ]);
                    await PrinterImin.sendRAWDataHexStr('0A');
                    await PrinterImin.printAndFeedPaper(100);
                    await PrinterImin.sendRAWDataHexStr('0A');
                  }}
                >
                  printColumnsText
                </Text>
              </Col>
              <Col span={12}>
                <Text
                  style={styles.item}
                  onPress={async () => {
                    await getMediaFilePermission();
                    await PrinterImin.printSingleBitmap(base64Img);
                    await PrinterImin.sendRAWDataHexStr('0A');
                    await PrinterImin.printAndFeedPaper(100);
                    await PrinterImin.sendRAWDataHexStr('0A');
                  }}
                >
                  printSingleBitmap
                </Text>
              </Col>
              <Col span={12}>
                <Text
                  style={styles.item}
                  onPress={async () => {
                    await getMediaFilePermission();
                    await PrinterImin.printMultiBitmap([base64Img, base64Img], {
                      align: IminPrintAlign.center,
                      width: 220,
                      height: 40,
                    });
                    await PrinterImin.sendRAWDataHexStr('0A');
                    await PrinterImin.printAndFeedPaper(100);
                    await PrinterImin.sendRAWDataHexStr('0A');
                  }}
                >
                  printMultiBitmap
                </Text>
              </Col>
              <Col span={12}>
                <Text
                  style={styles.item}
                  onPress={async () => {
                    await PrinterImin.printSingleBitmapBlackWhite(base64Img);
                    await PrinterImin.printAndFeedPaper(100);
                  }}
                >
                  printSingleBitmapBlackWhite
                </Text>
              </Col>
              <Col span={12}>
                <Text
                  style={styles.item}
                  onPress={async () => {
                    await PrinterImin.printBarCode(
                      IminBarcodeType.jan13,
                      '0123456789012',
                      {
                        align: IminPrintAlign.center,
                        position: IminBarcodeTextPos.aboveText,
                      }
                    );
                    await PrinterImin.printAndFeedPaper(100);
                    await PrinterImin.sendRAWDataHexStr('0A');
                  }}
                >
                  printBarCode
                </Text>
              </Col>
              <Col span={12}>
                <Text
                  style={styles.item}
                  onPress={async () => {
                    await PrinterImin.printQrCode('https://www.imin.sg', {
                      align: IminPrintAlign.center,
                      errorCorrectionLevel: IminQrcodeCorrectionLevel.levelH,
                      qrSize: 4,
                    });
                    await PrinterImin.sendRAWDataHexStr('0A');
                  }}
                >
                  printQrCode
                </Text>
              </Col>
              <Col span={12}>
                <Text
                  style={styles.item}
                  onPress={async () => {
                    await PrinterImin.printDoubleQR(
                      { text: '123456', level: 0, leftMargin: 10, version: 0 },
                      { text: '123456', level: 0, leftMargin: 100, version: 6 },
                      4
                    );
                    await PrinterImin.printAndFeedPaper(100);
                    await PrinterImin.sendRAWDataHexStr('0A');
                  }}
                >
                  printDoubleQR
                </Text>
              </Col>
              <Col span={12}>
                <Text
                  style={styles.item}
                  onPress={async () => await PrinterImin.partialCut()}
                >
                  partialCut
                </Text>
              </Col>
              <Col span={12}>
                <Text
                  style={styles.item}
                  onPress={async () => await PrinterImin.openCashBox()}
                >
                  openCashBox
                </Text>
              </Col>
              <Col span={12}>
                <Text
                  style={styles.item}
                  onPress={async () => {
                    if (open) {
                      setOpen(!open);
                    }
                    await PrinterImin.openLogs(Number(open));
                  }}
                >
                  openLogs
                </Text>
              </Col>
              <Col span={12}>
                <Text
                  style={styles.item}
                  onPress={async () => {
                    if (open) {
                      setOpen(!open);
                    }
                    await PrinterImin.openLogs(Number(open));
                  }}
                >
                  openLogs
                </Text>
              </Col>

              <Col span={12}>
                <Text
                  style={styles.item}
                  onPress={async () => {
                    if (open) {
                      setOpen(!open);
                    }
                    await PrinterImin.openLogs(Number(open));
                  }}
                >
                  openLogs
                </Text>
              </Col>
            </Row>
          </Card>
        </Space>
      </ScrollView>
      <Dialog.Keyboard
        title="Print Text"
        visible={dialogState.cDialog.show}
        showConfirmButton={false}
        showCancelButton={false}
        closeOnPressOverlay={false}
        showClose
        onPressClose={closeDialog}
      >
        <Blank top={24} bottom={32} left={24} right={24}>
          <TextInput
            placeholder="Please enter content"
            bordered
            clearable
            type="textarea"
            showWordLimit
            maxLength={500}
            onChange={(text) => {
              setDialogState((s) => ({
                ...s,
                cDialog: {
                  ...s.cDialog,
                  value: text,
                  show: true,
                },
              }));
            }}
            rows={4}
            value={dialogState.cDialog.value}
          />
        </Blank>
        <Blank bottom={24} left={24} right={24}>
          <Button text="确定" type="primary" onPress={handleConfirm} />
        </Blank>
      </Dialog.Keyboard>
    </>
  );
}

const styles = StyleSheet.create({
  item: {
    flex: 1,
    height: 100,
    lineHeight: 90,
    textAlign: 'center',
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 10,
    borderStyle: 'solid',
    marginVertical: 5,
  },
});
const base64Img = `data:image/png;base64,`;
